import Footer from "./Components/Footer";
import Header from "./Components/Header";
import Todos from "./Components/Todos";
import React,{useState} from "react";
import AddTodo from "./Components/AddTodo";

function App() {
  const onDelete=(todo)=>{
    console.log("I am deleting ",todo);

    // Direct Slicing will not work

    setTodos(todos.filter((e)=>{
      return e!==todo
    }))
  }

  const addTodo=(title,desc)=>{
    console.log("I am adding this todo",title,desc);
    let sno;
    sno=todos[todos.length()-1].sno +1;

    const myTodo={
      sno:sno,
      title:title,
      desc:desc,
    }
    setTodos([...todos,myTodo]);
    console.log(myTodo);
  }



  const[todos,setTodos]=useState([
    {
      sno:1,
      title: "Go to Market",
      desc: "Bring fruits and vegetable from market, nothing to eat at home now."
    },
    {
      sno:2,
      title: "Go to Library",
      desc: "Learn some new concepts of Data Structure and Algorithms."
    },
    {
      sno:3,
      title: "Complete your Project",
      desc: "Time is running ,finish projects as soon as possible there are many other things to do also."
    }
  ]);

  return (
    <>
    <Header title="To-Do-List" seacrhBar={false}/>
    <AddTodo addTodo={addTodo}/>
    <Todos todos={todos} onDelete={onDelete}/>
    <Footer/>
    </>
  );
}

export default App;
